function localStoragee()
{
	if (typeof(Storage) !== "undefined") //če lahko brskalnik ima local storage
	{
		if (p) //samo prvič
		{
			p = 0;
			for (var r = 0; r <= 4; r++)
			{
				pravilni[r] = Number(localStorage.getItem("pravilni" + r)) || 0; //iz local storiga dobi vrednosti za pravilneglagole in štglagolov, če jih ni je 0
				steviloGlagolov[r] = Number(localStorage.getItem("steviloGlagolov" + r)) || 0;
			}
		}
		else
		{
			for (var r = 0; r <= 4; r++)
			{
				localStorage.setItem("pravilni" + r, pravilni[r]); //vsakič pa nove vrednosti zapiše v local storage
				localStorage.setItem("steviloGlagolov" + r, steviloGlagolov[r]);
			}
		}
	}
}

